fun filterEquipments(jsonString: String): MutableList<String> {
    val gson = Gson()
    val equipments: List<Equipment> = gson.fromJson(jsonString, Array<Equipment>::class.java).toList()

    val filterResultList = mutableListOf<String>()

    for (equipment in equipments) {
        filterResultList.add(APIRemote.dataFilter(equipment))
    }

    return filterResultList
}